import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './MyBookings.css'; 

const MyBooking = () => {
  const [mybookings,setmybookings ] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchmybookings = async () => {
      try {
        const response = await axios.get('http://localhost:3002/api/booking/bookings/user', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`, // Use token from local storage or context
          },
        });
        setmybookings(response.data);
      } catch (error) {
        console.error('Error fetching listenings:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchmybookings();
  }, []);

  
  
  return (
    <div className="mybooking-container">
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="mybooking-list">
          {mybookings.length > 0 ? (
            mybookings.map((mybookings) => (
              <div key={mybookings._id} className="mybooking-item">
                <h3>Hire: {mybookings.hire?.departure} to {mybookings.hire?.arrival}</h3>
                <p><strong>Seats Booked:</strong> {mybookings.seatsBooked}</p>
              
                <p><strong>Date:</strong> {new Date(mybookings.hire?.date).toLocaleDateString()}</p>
                <p><strong>Vehicle Type:</strong> {mybookings.hire?.vehicleType}</p>


                <p><strong>driver:</strong> {mybookings.user?.firstname}</p>

              </div>
            ))
          ) : (
            <p>No bookings found.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default MyBooking;
