import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './AllTravellerListenings.css'; // Import your CSS file

const AllTravellerListenings = () => {
  const [listenings, setListenings] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchListenings = async () => {
      try {
        const response = await axios.get('http://localhost:3002/api/travellistening/getalllistenings', {
          headers: {
            Authorization: `Bearer ${localStorage.getItem('token')}`, // Use token from local storage or context
          },
        });
        setListenings(response.data);
      } catch (error) {
        console.error('Error fetching listenings:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchListenings();
  }, []);

  return (
    <div className="all-traveller-listenings-container">
      {loading ? (
        <p>Loading...</p>
      ) : (
        <div className="traveller-listenings-list">
          {listenings.length > 0 ? (
            listenings.map((listening) => (
              <div key={listening._id} className="traveller-listening-item">
                <p><strong>Phone:</strong> {listening.phone}</p>
                <p><strong>Departure:</strong> {listening.departure}</p>
                <p><strong>Arrival:</strong> {listening.arrival}</p>
                <p><strong>Date:</strong> {new Date(listening.date).toLocaleDateString()}</p>
              </div>
            ))
          ) : (
            <p>No traveler listenings found.</p>
          )}
        </div>
      )}
    </div>
  );
};

export default AllTravellerListenings;
